#include <iostream>
#include <string>

int main() {
    int age = 22;                          // 4 bytes
    float temperature = 98.6f;             // 4 bytes
    double price = 19.99;                  // 8 bytes
    char initial = 'G';                    // 1 byte
    bool isOpen = true;                    // 1 byte
    std::string name = "GenSpark";         // Variable bytes

    std::cout << "Name: " << name << "\n";
    std::cout << "Age: " << age << " (" << sizeof(age) << " bytes)" << "\n";
    std::cout << "Temperature: " << temperature << " (" << sizeof(temperature) << " bytes)" << "\n";
    std::cout << "Price: " << price << " (" << sizeof(price) << " bytes)" << "\n";
    std::cout << "Initial: " << initial << " (" << sizeof(initial) << " byte)" << "\n";
    std::cout << "Is Open: " << isOpen << " (" << sizeof(isOpen) << " byte)" << "\n";
    return 0;
}

#include <iostream>

int main() {
    int a = 10, b = 3;

    std::cout << "Sum: " << (a + b) << "\n";
    std::cout << "Remainder: " << (a % b) << "\n";
    std::cout << "Is a > b? " << (a > b) << " (1 means true)";

    return 0;
}

