#include <iostream>

int main() {
    int num;
    std::cout << "Enter a number: ";
    std::cin >> num;

    if (num > 0) {
        std::cout << "Positive";
    } else if (num < 0) {
        std::cout << "Negative";
    } else {
        std::cout << "Zero";
    }

    return 0;
}

#include <iostream>

int main() {
    std::cout << "Counting to 5:\n";
    for (int i = 1; i <= 5; i++) {
        std::cout << i << " ";
    }

    std::cout << "\n\nEven numbers up to 10:\n";
    int j = 2;
    while (j <= 10) {
        std::cout << j << " ";
        j += 2;
    }

    return 0;
}

#include <iostream>

// Global function declaration
int add(int a, int b) {
    return a + b;
}

void greet() {
    std::cout << "Welcome to C++ Functions!\n";
}

int main() {
    greet();
    int result = add(10, 20);
    std::cout << "Sum is: " << result;
    return 0;
}

