#include <iostream>
#include <string>

template <typename T>
void swapValues(T &a, T &b) {
    T temp = a;
    a = b;
    b = temp;
}

int main() {
    int x = 10, y = 20;
    swapValues(x, y);
    std::cout << "x: " << x << ", y: " << y;
    return 0;
}
