#include <iostream>

// Recursive function to calculate Factorial
int factorial(int n) {
    if (n <= 1) return 1; // Base Case
    return n * factorial(n - 1); // Recursive Step
}

int main() {
    int num = 5;
    std::cout << "Factorial of " << num << " is: " << factorial(num);
    return 0;
}

// Simple Palindrome Check using Two Pointers
#include <iostream>
#include <string>

bool isPalindrome(std::string s) {
    int left = 0, right = s.length() - 1;
    while (left < right) {
        if (s[left] != s[right]) return false;
        left++;
        right--;
    }
    return true;
}

int main() {
    std::string test = "racecar";
    std::cout << "Is '" << test << "' a palindrome? " << (isPalindrome(test) ? "Yes" : "No");
    return 0;
}

