// Concept: Recursion with Backtracking
#include <iostream>
#include <vector>

void findSubsets(std::vector<int>& arr, std::vector<int>& current, int index) {
    if (index == arr.size()) {
        // Print current subset
        return;
    }
    // Include element
    current.push_back(arr[index]);
    findSubsets(arr, current, index + 1);

    // Backtrack: Exclude element
    current.pop_back();
    findSubsets(arr, current, index + 1);
}

int main() {
    std::vector<int> nums = {1, 2};
    std::vector<int> path;
    findSubsets(nums, path, 0);
    return 0;
}

