// Fibonacci with Memoization (O(n))
#include <iostream>
#include <vector>

long long fib(int n, std::vector<long long>& memo) {
    if (n <= 1) return n;
    if (memo[n] != -1) return memo[n]; // Return cached result
    return memo[n] = fib(n - 1, memo) + fib(n - 2, memo);
}

int main() {
    int n = 50;
    std::vector<long long> memo(n + 1, -1);
    std::cout << "50th Fibonacci: " << fib(n, memo);
    return 0;
}

// Concept: DSU Find with Path Compression
#include <iostream>
#include <vector>

struct DSU {
    std::vector<int> parent;
    DSU(int n) {
        parent.resize(n);
        for(int i = 0; i < n; i++) parent[i] = i;
    }
    int find(int i) {
        if (parent[i] == i) return i;
        return parent[i] = find(parent[i]); // Path Compression
    }
};

int main() {
    DSU d(5);
    std::cout << "Parent of 3: " << d.find(3);
    return 0;
}

// Finding the only non-repeating element using XOR
#include <iostream>
#include <vector>

int findUnique(std::vector<int>& nums) {
    int res = 0;
    for(int n : nums) res ^= n;
    return res;
}

int main() {
    std::vector<int> data = {1, 2, 3, 2, 1};
    std::cout << "Unique element: " << findUnique(data);
    return 0;
}

