const express = require('express');
const app = express();

app.get('/api/greet', (req, res) => {
    res.json({ message: 'Welcome to the Fullstack Lab!' });
});

console.log('Server running on port 3000');

const mongoose = require('mongoose');

const Product = mongoose.model('Product', { name: String, price: Number });

async function addProduct() {
    const p = new Product({ name: 'Laptop', price: 999 });
    await p.save();
    console.log('Product Saved!');
}

const bcrypt = require('bcrypt');
const jwt = require('jsonwebtoken');

async function register(password) {
    const hash = await bcrypt.hash(password, 10);
    console.log('Secure Hash:', hash);

    const token = jwt.sign({ id: 123 }, 'top_secret', { expiresIn: '1h' });
    console.log('Access Token:', token);
}

// Example Controller
exports.getUser = async (req, res, next) => {
    try {
        const user = await UserService.findById(req.params.id);
        if (!user) throw new Error('Not found');
        res.status(200).json(user);
    } catch (err) {
        next(err); // Send to error middleware
    }
};

// RTK Slice Example
import { createSlice } from '@reduxjs/toolkit';

const cartSlice = createSlice({
  name: 'cart',
  initialState: { items: [], total: 0 },
  reducers: {
    addItem: (state, action) => {
      state.items.push(action.payload);
    }
  }
});

// Simple Jest Test
const sum = (a, b) => a + b;

test('adds 1 + 2 to equal 3', () => {
  expect(sum(1, 2)).toBe(3);
});

/**
 * Final Graduation Script: App Health Dashboard
 * Demonstrates: React Hooks + API Fetching + Clean UI
 */
import React, { useEffect, useState } from 'react';

const Dashboard = () => {
    const [status, setStatus] = useState('Checking...');

    useEffect(() => {
        fetch('/api/health')
            .then(res => res.json())
            .then(data => setStatus(data.status))
            .catch(() => setStatus('Offline'));
    }, []);

    return (
        <div className="dashboard">
            <h1>System Status: {status}</h1>
            <p>Congratulations on your journey!</p>
        </div>
    );
};
