# Program: Check if number is positive, negative, or zero
num = int(input('Enter a number: '))

if num > 0:
    print('Positive')
elif num < 0:
    print('Negative')
else:
    print('Zero')

# Nested if: Check age and student status
age = int(input('Enter age: '))
is_student = input('Are you a student? (yes/no): ').lower() == 'yes'

if age < 18:
    print('You are underage')
else:
    if is_student:
        print('Adult student')
    else:
        print('Adult')

# Loop demonstrations
print('=== for loop ===')
for i in range(3):
    print(f'Iteration {i}')

print('\n=== while loop ===')
count = 0
while count < 3:
    print(f'Count: {count}')
    count += 1

print('\n=== break ===')
for i in range(10):
    if i == 5:
        break
    print(i)

print('\n=== continue ===')
for i in range(5):
    if i == 2:
        continue
    print(i)

print('\n=== nested loops ===')
for i in range(2):
    for j in range(3):
        print(f'i={i}, j={j}')

def create_user(name, age=0, **kwargs):
    print(f'Name: {name}')
    print(f'Age: {age}')
    for key, value in kwargs.items():
        print(f'{key}: {value}')

create_user('Alice', 25, city='NYC', job='Engineer')

def divide_safe(a, b):
    if b == 0:
        return None
    return a / b

def get_user_info():
    return 'Alice', 25, 'NYC'

print(divide_safe(10, 2))   # 5.0
print(divide_safe(10, 0))   # None

name, age, city = get_user_info()
print(f'{name} ({age}) from {city}')

class Dog:
    def __init__(self, name, breed):
        self.name = name
        self.breed = breed

    def bark(self):
        return f'{self.name} says: Woof!'

    def info(self):
        return f'{self.name} is a {self.breed}'

dog = Dog('Buddy', 'Golden Retriever')
print(dog.info())
print(dog.bark())

class Animal:
    def __init__(self, name):
        self.name = name

    def speak(self):
        return 'Some sound'

class Dog(Animal):
    def speak(self):
        return f'{self.name} barks'

class Cat(Animal):
    def speak(self):
        return f'{self.name} meows'

dog = Dog('Rex')
cat = Cat('Whiskers')

print(dog.speak())
print(cat.speak())

def divide(a, b):
    try:
        result = a / b
    except ZeroDivisionError:
        return 'Error: Cannot divide by zero'
    except TypeError:
        return 'Error: Invalid input type'
    else:
        return f'Result: {result}'
    finally:
        print('Division attempt completed')

print(divide(10, 2))
print(divide(10, 0))
print(divide('a', 2))

