def limit_calls(max_calls):
    def decorator(func):
        def wrapper():
            if wrapper.calls < max_calls:
                wrapper.calls += 1
                return func()
            return 'Limit reached'
        wrapper.calls = 0
        return wrapper
    return decorator

@limit_calls(3)
def greet():
    return 'Hello!'

print(greet())  # Hello!
print(greet())  # Hello!
print(greet())  # Hello!
print(greet())  # Limit reached

