import time

def timer_decorator(func):
    def wrapper(*args):
        start = time.time()
        result = func(*args)
        end = time.time()
        print(f'Execution time: {end - start:.4f} seconds')
        return result
    return wrapper

@timer_decorator
def slow_function(n):
    time.sleep(n)
    return 'Done'

slow_function(1)

from flask import Flask, jsonify, request

