import json
import os

class TodoApp:
    def __init__(self, filename='todos.json'):
        self.filename = filename
        self.todos = self.load()

    def load(self):
        if os.path.exists(self.filename):
            with open(self.filename) as f:
                return json.load(f)
        return []

    def save(self):
        with open(self.filename, 'w') as f:
            json.dump(self.todos, f)

    def add(self, title):
        self.todos.append({'id': len(self.todos), 'title': title, 'done': False})
        self.save()

    def list_todos(self):
        for todo in self.todos:
            status = '✓' if todo['done'] else ' '
            print(f'[{status}] {todo["title"]}')

    def complete(self, todo_id):
        self.todos[todo_id]['done'] = True
        self.save()

app = TodoApp()
app.add('Learn Python')
app.add('Build project')
app.list_todos()
