        StringBuilder sb = new StringBuilder();
        int i = 0;
        while (i < s.length()) {
            int j = i; char c = s.charAt(i);
            while (j < s.length() && s.charAt(j) == c) j++;
            sb.append(c); if(j-i>1) sb.append(j-i);
            i = j;
        }
        System.out.println(sb.length() < s.length() ? sb.toString() : s);
    }
}

